const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const storeys = require('minecraft-storeys');

const log = require('../../util/log');

const blockIconURI = 'data:image/png;base64,';

class Minecraft {
    constructor() {
        this.eventsReceived = [];
        this.registeredConditions = new Set();
        this.playerLastJoined = undefined;
        this.effectedPlayer = undefined;

        const urlParams = global.settings || new URL(window.location.href).searchParams;

        this._onConnect = new storeys.MinecraftProvider(urlParams.get('eventBusURL'), urlParams.get('code')).connect();
        this._onConnect.then(minecraft => {
            this.minecraft = minecraft;
            this.effectedPlayer = minecraft.loggedInPlayer;
            this.minecraft.whenPlayerJoins(this.effectedPlayer).subscribe(result => {
                this.playerLastJoined = result.player;
                this.eventsReceived["Player joins"] = true;
            });
        });

        this._onConnect.then.bind(this);
    }

    /**
     * @return {object} This extension's metadata.
     */
    getInfo() {
        return {
            id: 'minecraft',
            name: 'Minecraft',
            menuIconURI: blockIconURI,
            blocks: [
                {
                    opcode: 'whenEvent',
                    blockType: BlockType.HAT,
                    text: 'when [EVENT]',
                    arguments: {
                        EVENT: {
                            type: ArgumentType.STRING,
                            menu: 'events',
                            defaultValue: 'playerJoined'
                        }
                    }
                },
                {
                    opcode: 'whenEntity',
                    blockType: BlockType.HAT,
                    text: 'when [ENTITY] right clicked',
                    arguments: {
                        ENTITY: {
                            type: ArgumentType.STRING,
                            defaultValue: 'Entity'
                        }
                    }
                },
                {
                    opcode: 'whenCommand',
                    blockType: BlockType.HAT,
                    text: 'when /[COMMAND]',
                    arguments: {
                        COMMAND: {
                            type: ArgumentType.STRING,
                            defaultValue: 'demo'
                        }
                    }
                },
                {
                    opcode: 'whenInside',
                    blockType: BlockType.HAT,
                    text: 'when inside [NAME]',
                    arguments: {
                        NAME: { type: ArgumentType.STRING, defaultValue: 'name'}
                    }
                },
                {
                    opcode: 'narrate',
                    blockType: BlockType.COMMAND,
                    text: '[ENTITY] speaks [TEXT]',
                    arguments: {
                        ENTITY: {
                            type: ArgumentType.STRING,
                            defaultValue: 'Entity'
                        },
                        TEXT: {
                            type: ArgumentType.STRING
                        }
                    }
                },
                {
                    opcode: 'minecraftCommand',
                    blockType: BlockType.COMMAND,
                    text: '/[COMMAND]',
                    arguments: {
                        COMMAND: {
                            type: ArgumentType.STRING,
                            defaultValue: 'demo'
                        }
                    }
                },
                {
                    opcode: 'showTitle',
                    blockType: BlockType.COMMAND,
                    text: 'title [TEXT]',
                    arguments: {
                        TEXT: {
                            type: ArgumentType.STRING,
                            defaultValue: 'Welcome!'
                        }
                    }
                },
                {
                    opcode: 'getPlayerItemHeld',
                    blockType: BlockType.REPORTER,
                    text: 'Item held'
                },
                {
                    opcode: 'getLastPlayerJoined',
                    blockType: BlockType.REPORTER,
                    text: 'Last joined Player'
                },
                {
                    opcode: 'getItem',
                    blockType: BlockType.REPORTER,
                    text: '[ITEM]',
                    arguments: {
                        ITEM: {
                            type: ArgumentType.STRING,
                            menu: 'items',
                            defaultValue: 'Apple'
                        }
                    }
                }
            ],
            menus: {
                events: [
                    {
                        value: 'playerJoined',
                        text: 'Player joins'
                    }
                ],
                items: this._getItemNames()
            },
            // translations
            translation_map: {
                fr: {
                },
                de: {
                },
                nl: {
                    // 'whenEvent': 'Als [EVENT]',
                    // 'events_playerJoined': 'Speler joined',
                    // 'items_Nothing' : 'Niks',
                    // 'items_Apple' : 'Appel',
                    // 'items_Beef' : 'Rund',
                    // 'items_Beetroot' : 'Bietje',
                    // 'items_Boat' : 'Boot',
                    // 'items_Book' : 'Boek',
                    // 'items_Bow' : 'Boog',
                    // 'items_Bowl' : 'Schaal',
                    // 'items_Bread' : 'Brood',
                    // 'items_Cactus' : 'Kactus',
                    // 'items_Cake' : 'Cake',
                    // 'items_Carrot' : 'Wortel',
                    // 'items_Cauldron' : 'Cauldron',
                    // 'items_Chicken' : 'Kip',
                    // 'items_Clock' : 'Klok',
                    // 'items_Cookie' : 'Koekje'

                }
            }
        };
    }

    _getItemNames() {
        return Object.keys(storeys.ItemType).map(name => ({
            text: name,
            value: name
        }));
    }

    whenEvent(event) {
        const was = this.eventsReceived[event];
        this.eventsReceived[event] = false;
        return was || false;
    }

    _whenCondition(method, ...args) {
        const eventName = method + args;
        if (!this.registeredConditions.has(eventName)) {
            this.registeredConditions.add(eventName);
            this._onConnect.then(() => {
                this.minecraft[method].apply(this.minecraft, args).then(register => {
                    register.on().subscribe((data) => {
                        var loggedInPlayer = this.effectedPlayer;
                        this.effectedPlayer = data.playerUUID;
                        setTimeout(() => this.effectedPlayer = loggedInPlayer, 2000);
                        this.eventsReceived[eventName] = true;
                    });
                });
            });
        }
        return this.whenEvent(eventName);
    }

    whenEntity(args) {
        return this._whenCondition('whenEntityRightClicked', args.ENTITY);
    }

    whenInside(args) {
        return this._whenCondition('whenInside', this.effectedPlayer, args.NAME);
    }

    whenCommand(args) {
        this._onConnect.then(() => this._whenCondition('whenCommand', args.COMMAND));
    }

    getPlayerItemHeld() {
        this._onConnect.then(() => this.minecraft.getItemHeld(this.effectedPlayer, storeys.HandType.MainHand));
    }

    getLastPlayerJoined() {
        return this.playerLastJoined;
    }

    getItem(args) {
        return args.ITEM;
    }

    narrate(args) {
        this._onConnect.then(() => this.minecraft.narrate(this.effectedPlayer, args.ENTITY, args.TEXT).then(() => log('narrate called'), err => log('error:', err)));
    }

    minecraftCommand(args) {
        this._onConnect.then(() => this.minecraft.runCommand(this.effectedPlayer, args.COMMAND).then(() => log('command called'), err => log('error:', err)));
    }

    showTitle(args) {
        this._onConnect.then(() => this.minecraft.showTitle(this.effectedPlayer, args.TEXT).then(() => log('showTitle called'), err => log('error:', err)));
    }
}

module.exports = Minecraft;
